a = [1, -20, 38, 0, 44]      #вводим список данныхb = [88, -20, 48, 4, 33, 2]if len(a) > len(b): #сравниваем длину одного списка с другим и выбираем наименьший из них    n = len(b)else:    n = len(a)        for i in range(n): #перебираем все индексы от нулевого до n        if abs(a[i] - b[i]) < 15:            print ('congrats!')        else:            if a[i] > b[i]: #сравниваем числа и выводим на экран минимальное                print (b[i])            else:                print (a[i])